select  Employee.Id, Gender.id, FullName.First_Name, FullName.Last_Name, FullName.Patronymic, [Employment Date], [Date Of Birth],  PositionName.Id, Position.Salary, Address.Country, Address.Region, Address.City, Address.Street, Address.[Street Number], Address.[Apartment Number], [Phone Number], Status.Id from Employee inner join Address on Employee.Address = Address.Id inner join FullName on Employee.[Full Name] = FullName.Id inner join Gender on Employee.Gender = Gender.Id inner join Position on Employee.Position = Position.Id inner join PositionName on Position.PositionName = PositionName.Id inner join Status on Employee.Status = Status.Id where Employee.Id = 1
select * from Employee